# -*- coding: utf-8 -*-

import gom
import os 
import numpy as np

# Vorraussetzungen für das Skript
# Hüllzylinder nach Maschinenkoordinaten anlegen => 'Hüllzylinder Y-Achse' mit dem Richtungsvektor in Richtung der positiven Y-Achse festlegen 
#                                                => 'Hüllzylinder neg. X-Achse' mit dem Richtungsvektor in Richtung der negativen X-Achse festlegen

#Widget zur Abfrage des Biegeradius
RESULT12=gom.script.sys.execute_user_defined_dialog (file='Widget12 Biegeradius.gdlg')
Biegeradius=RESULT12.input

# Biegewinkel konstruieren 
MCAD_ELEMENT=gom.script.inspection.create_angle_by_2_directions (
	direction1={'glue_transformed': False, 'inverted': True, 'target': gom.app.project.actual_elements['Hüllzylinder Y-Achse'], 'type': 'direction'}, 
	direction2=gom.app.project.actual_elements['Hüllzylinder neg. X-Achse'], 
	name='Biegewinkel', 
	properties=gom.Binary (''))

# Tangentenschnittpunkt konstruieren 
MCAD_ELEMENT=gom.script.primitive.create_point_by_line_intersection (
	intersect_element={'projection_type': 'line', 'target': gom.app.project.actual_elements['Hüllzylinder Y-Achse']}, 
	line=gom.app.project.actual_elements['Hüllzylinder neg. X-Achse'], 
	name='Schnittpunkt Tangenten', 
	point_number=1.0, 
	properties=gom.Binary (''))

# Biegewinkel Wert erfassen
MCAD_ELEMENT=gom.script.inspection.inspect_dimension (
	angle_restriction='xyz', 
	elements=[gom.app.project.actual_elements['Biegewinkel']], 
	nominal_value=0.0, 
	nominal_value_source='fixed_value', 
	name='Biegewinkel_Wert',
	properties=gom.Binary (''), 
	type='angle')

Biegewinkel = gom.app.project.inspection['Biegewinkel_Wert'].get ('result_dimension.measured_value')
#print(Biegewinkel/2)

gom.script.view.set_tab_visible (
	view='right_docking_area', 
	visible=False)

# Drehachse konstruieren 
MCAD_ELEMENT=gom.script.primitive.create_line_by_cross_product (
	direction1=gom.app.project.actual_elements['Hüllzylinder neg. X-Achse'], 
	direction2=gom.app.project.actual_elements['Hüllzylinder Y-Achse'], 
	name='Drehachse', 
	point=gom.app.project.actual_elements['Schnittpunkt Tangenten'], 
	properties=gom.Binary (''))

# Winkelhalbierende konstruieren
MCAD_ELEMENT=gom.script.primitive.create_line_by_rotation (
	angle=Biegewinkel/2, 
	axis={'glue_transformed': False, 'inverted': True, 'target': gom.app.project.actual_elements['Drehachse'], 'type': 'direction'}, 
	direction={'glue_transformed': False, 'inverted': True, 'target': gom.app.project.actual_elements['Hüllzylinder Y-Achse'], 'type': 'direction'}, 
	name='Winkelhalbierende 1', 
	point=gom.app.project.actual_elements['Schnittpunkt Tangenten'], 
	properties=gom.Binary (''), 
	rotation_direction='clockwise')

# neuen Koordinatenursprungspunkt konstruieren

MCAD_ELEMENT=gom.script.primitive.create_offset_point (
	direction=gom.app.project.actual_elements['Winkelhalbierende 1'], 
	name='Koordinatensystem_Ursprung', 
	offset=Biegeradius/np.sin(Biegewinkel/2), 
	point=gom.app.project.actual_elements['Schnittpunkt Tangenten'], 
	properties=gom.Binary (''))

# Initiale Ausrichtung festlegen
gom.script.manage_alignment.define_original_alignment_as_initial_alignment (part=gom.app.project.parts['Bauteil'])

# Koordinatensystem zur neuen Ausrichtung erzeugen 
MCAD_ELEMENT=gom.script.cs.create_element_by_geometric_elements (
	additional_constraints={'use_fixed_opening_angle': 'true'}, 
	computation_mode='pure-geometry', 
	datum_1_elements=[gom.app.project.actual_elements['Koordinatensystem_Ursprung']], 
	datum_2_elements=[gom.app.project.actual_elements['Hüllzylinder Y-Achse']], 
	datum_3_elements=[gom.app.project.actual_elements['Hüllzylinder neg. X-Achse']], 
	direction_1='z', 
	direction_2='x_minus', 
	inclusion=False, 
	properties=gom.Binary (''))

# Ausrichtung über Koordinatensystem erzeugen 
CAD_ALIGNMENT=gom.script.alignment.create_by_csys (
	actual_coordinate_system=gom.app.project.actual_elements['Koordinatensystem_Ursprung|Hüllzylinder Y-Achse|Hüllzylinder neg. X-Achse'], 
	name_expression='Ausrichtung über Koordinatensysteme', 
	parent_alignment=gom.app.project.parts['Bauteil'].original_alignment, 
	target_coordinate_system=gom.app.project.nominal_elements['system_global_coordinate_system'])
	
	


